-- 테이블 ant5.board 구조 내보내기
CREATE TABLE IF NOT EXISTS `board` (
  `bno` int(11) NOT NULL AUTO_INCREMENT,
  `member_no` int(11) NOT NULL DEFAULT 0,
  `title` varchar(50) CHARACTER SET utf8 NOT NULL,
  `content` varchar(1000) CHARACTER SET utf8 NOT NULL,
  `file` longtext CHARACTER SET utf8 NOT NULL,
  `actor` varchar(150) CHARACTER SET utf8 NOT NULL,
  `genre` varchar(50) CHARACTER SET utf8 NOT NULL,
  `runtime` varchar(50) NOT NULL DEFAULT '',
  `date` timestamp NOT NULL DEFAULT current_timestamp(),
  `rundate` varchar(50) NOT NULL,
  `grade` varchar(50) NOT NULL,
  `type` varchar(1) NOT NULL DEFAULT '0',
  `views` int(100) NOT NULL DEFAULT 0,
  PRIMARY KEY (`bno`) USING BTREE,
  KEY `member_no` (`member_no`),
  CONSTRAINT `FK_board_member` FOREIGN KEY (`member_no`) REFERENCES `member` (`member_no`)
) ENGINE=InnoDB AUTO_INCREMENT=102 DEFAULT CHARSET=utf8mb4;

