INSERT INTO `board` (`bno`, `member_no`, `title`, `content`, `file`, `actor`, `genre`, `runtime`, `date`, `rundate`, `grade`, `type`, `views`) VALUES
	(15, 1, '데스트랩', '한 때 잘나갔던 극작가 시드니 브륄은 신작을 쓰는데 어려움을 겪고 있다. 어느 날 시드니는 자신의 세미나를 들었던 학생 클리포드 앤더슨으로부터 의견을 구하기 위해 보낸 대본 <데스트랩>을 받게 되고, 너무나 잘 쓰여진 대본에 질투를 느낀다. 시드니는 이 작품을 손에 넣기 위해 클리포드를 자신의 작업실로 초대하고 시드니의 아내 마이라는 시드니의 계획에 불안함을 느끼는데...', 'deathtrap.jpg', '고영빈, 조영규, 박민성, 송유택, 기세중, 차서원, 이지현, 조한나, 이현진, 김지혜, 선한국', '스릴러', '120분', '2021-04-19 09:49:11', '2021.03.30 - 06.06', '14세 이상 관람가', '3', 23),
	(16, 1, '한뼘사이', '날 좋아하는 거야 뭐야? 연인인 듯 친구인 듯.. 나만 설레는 건가? 무뚝뚝한 여자와 썸을 넘지 못하는 답답한 남자의 만남. 닮은 듯 아닌 듯 한 두 남녀 사이의 한뼘사이. 아픔을 대처하는 방법이 다른 두 남녀 더 용감하게 밝게 이겨내는 여자와 어차피 망한 인생, 갈 때까지 가는 어설픈 사기꾼의 만남. 서로 다른 두 남녀 사이의 한뼘사이. 닿을 듯 말 듯 한 한뼘사이에서 이들의 아슬아슬 알콩달콩한 이야기가 시작된다!', '한뼘사이.jpg', '차수영, 황지현, 고연경, 윤주희, 최상배, 한훈, 임현준, 이동구, 민유리, 허예슬, 강수인, 이현진, 임재원, 김종훈, 최상빈, 함원태, 이진호, 온정, 양민직, 문도현', '로맨스 코미디', '100분', '2021-04-19 11:17:10', '2017.3.1 - OPEN RUN', '12세이상', '3', 75),
	(17, 1, '단테 신곡 - 지옥편', '단테는 자신의 서재에서 다시는 경험하고 싶지 않은 과거의 끔찍한 여행에 대한 책을 저술하기 시작한다. 그것은 바로 모든 희망이 완전히 박탈되어버린 끔찍한 지옥에서의 경험인 것이다. 단테는 과거를 회상하며, 자신을 죽음의 위기에서 구해준 정신적 스승인 베르길리우스와 험난한 지옥으로의 여행을 시작하게 되는데...', '단테신곡 지옥편.jpg', '정동환, 한윤춘, 정수영, 이천영, 김찬강, 이담, 허웅, 조창원, 유수진, 조민형, 민인기, 이수형, 김서휘', '스릴러', '180분', '2021-04-19 11:23:29', '2021.5.7 - 5.16', '12세 이상 관람가', '3', 58),
	(18, 1, '옥탑방 고양이', '꿈을 이루기 위해 상경한 정은이와 옥탑방으로 이사한 경민이의 아찔한 로맨틱코미디!', '옥탑방고양이.jpg', '김정문, 김석주, 강병준, 유온, 김다정, 이유진, 김미리내, 곽예헌, 이진실, 김창일, 최성환, 최종민, 김세나, 이봄이, 김민지, 문서윤', '로맨틱코미디', '100분', '2021-04-19 11:31:50', 'OPEN RUN', '만 13세 이상 관람가', '3', 50),
	(19, 1, '연극라면', '때는 90년대!! 고등학교때부터 만나온 만수와 은실. 만수는 잘다니던 직장을 그만두고 라면집을 차리겠다고한다. 은실은 철없는 만수에게 화내며 헤어지자고한다. 풋풋하고 생각만해도 떨리는 행복했던 연애 초기는 어디로 가버린걸까? 라면 끓일 때 스프부터 넣어? 면부터 넣어? 남자는 어떻게 그래? 여자는 어떻게 그래? 입장차이가 좁혀지지않는 남자와 여자. 오랜시간 만나온 그들의 끝은?', '연극라면.jpg', '지인호, 한종두, 채현우, 유승옥, 박은경, 임윤서, 남기형, 이재혁, 박한솔, 서태인, 최온겸, 김가현, 김준영, 강현택, 김동현', '레트로 코미디', '100분', '2021-04-19 11:35:15', '2020.04.04 - OPEN RUN', '만11세 이상', '3', 9),
	(20, 1, '시간을 파는 상점', '소방대원으로 더불어 사는 삶을 실천하다 일찍 세상을 떠난 아빠의 뜻을 이업다은 주인공 온조. 온조는 인터넷 카페에 크로노스라는 닉네임으로 시간을 파는 상점을 오픈해 손님들의 어려운 일을 해결해주며 자신의 시간을 판다. 훔친 물건을 제자리에 돌려놓아 달라는 의뢰 등등 다양한 의뢰가 끊이지 않는다. 그러던 중 도난 사건에 휘말려 온조와 친구들은 위기에 빠지게 된다. 마지막 순간까지 알 수 없는 도난사건의 진실은 무엇일까?', '시간을 파는 상점.jpg', '정해빈, 신서진, 김보람, 신희진, 이세형, 이시운, 류권하, 김범수, 소유경, 김소민, 백지연, 윤호상, 강지훈, 유인상', '코미디, 성장', '80분', '2021-04-19 11:57:32', '2016.10.08 - OEPN RUN', '만 8세이상', '3', 5),
	(21, 1, '나의 PS 파트너', '수화기 너머로 각자의 연애와 일상 등 속 깊은 대화를 나누며 점점 가까워지는 두 사람... 첫 감정 이상의 무언가를 서로에게 느끼게 되고, 어느 날 남자를 다시 찾아온 전 여자친구... 이들은 과연 어떤 선택을 하게 될까?', '20210125-29162.jpg', '장경원, 홍기준, 이탁호, 이보라, 이유나, 오수빈, 박민수, 임현모, 도지훈, 윤다솜, 이설아, 석민주', '섹시 로맨스 코미디', '90분', '2021-04-19 12:03:21', '2018.03.08 - 2021.05.31', '만 18세 이상 관람가', '3', 0),
	(22, 1, '안녕, 여름', '설렘이란 감정보다는 익숙함이 더 친숙한 결혼 6년차 태민과 여름. 태민을 한결같이 사랑하며 챙기는 여름. 사랑이란 감정이 당연해진 탓일까? 태민은 아내의 행동과 잔소리가 귀찮게만 느껴진다. 여름은 아이를 갖기 위한 여행을 제안한다. 여느 날처럼 태민의 집으로 출근한 태민의 조수 동욱. 스타를 꿈꾸며 태민에게 프로필 사진을 부탁하는 란. 태민을 챙기기 위해 앞치마까지 입은 조지. 태민의 집에 모인 조지, 동욱, 란... 그리고 여름. 아내에게 대한 사랑 그리고 아내에게 잘하지 못한 안타까움. 소소한 일상 속에서 태민은 여름의 소중함을 깨닫게 되는데...', '다운로드.jpg', '정원조, 송용진, 장지후, 박혜나, 이예은, 남명렬, 조남희, 박준휘, 조훈, 반정모, 이지수, 박가은', '감동, 성장', '110분', '2021-04-19 12:09:24', '2021.04.27 - 2021.06.20', '17세 이상 관람가', '3', 0),
	(23, 1, '택시 안에서', '쾌활하고 유쾌한 택시 운전사 민수 그리고 하영과 소희 두 남녀의 운명적인 만남! 하영과 소희는 오랜 연애 끝에 이별을 하게되고 소희는 한국을 떠나기로 결심한다. 뒤 늦게 후회가 된 하영은 소희를 만나기 위해 공항으로 택시를 급히 타게 되는데 의미심장한 택시 기사 민수와 자신에게 주어진 엄청난 반전 앞에 당황할 수밖에 없는 하영의 운명은?', '다운로드 (1).jpg', '김록기, 전청일, 조일권, 김종현, 양승호, 박형준, 이하은, 성지호', '코미디', '100분', '2021-04-19 12:13:31', '2018.10.12 - OPEN RUN', '만 12세 이상', '3', 1),
	(24, 1, '사춘기 메들리', '정우는 한적한 시골 마을에 전학을 오게 된다. 정우는 다시 전학을 가게 될 것이라는 사실을 알게 된 후 관심 없던 아영에게 고백을 하고 덕원을 괴롭히던 일진에게도 결투를 신청한다. 집으로 돌아온 정우는 전학을 취소했다는 청천벽력 같은 소식을 듣는데... 앞으로 정우의 학교생활은 어떻게 될까??', '다운로드 (2).jpg', '김윤, 홍리나, 나도현, 김유진, 공소영, 유의봉, 김지수, 이찬우, 윤형석, 최호민, 유선봉, 김정환', '로맨스', '100분', '2021-04-19 12:16:38', '2018.03.10 - 2021.04.30', '7세 이상 관람가', '3', 6),
	(25, 1, '음악극 <디어 마이 스프링>', '그녀와 함께한 추억을 남기고 기억하려는 남자. 마음으로 세상을 보고 가슴으로 노래하는 여자', '다운로드 (3).jpg', '김류하, 추연성, 엄대현, 김미리내, 오해주, 박나연', '멜로 로맨스', '90분', '2021-04-19 12:19:42', '2021.04.02 - 2021.04.25', '13세 이상', '3', 9),
	(26, 1, '죽여주는 이야기', '비가 쏟아지는 날... 지하 깊은 그 곳에 그로테스크한 모습을 한 여자가 찾아온다. 그녀의 아이디는 마돈나... 그녀가 찾아간 곳은 다름이 아닌 한 자살 사이트 회장의 은밀한 실험실.. 그리고 그 곳에서 기다리고 있는 것 인터넷으로 알게 된 자살 사이트 회장, 수려한 외모의 소유자인 안락사!! 그리고 그녀가 부른 의문의 사나이 바보레옹과 그곳에서 벌어지는 기괴하지만 흥미롭고 유쾌한 상품 설명들! 살벌해 보이는 공간에 살벌해 보이는 인물들이 \'자살\'이라는 살벌한 이야기는 하는데 왜 이렇게 웃음이 터지는 건지! 연극을 통해 그 진실을 파헤쳐 보자!', '다운로드 (4).jpg', '이봉근, 유혜성, 노진욱, 임진혁, 조시연, 최혜린, 김주사랑 한서희, 박선하, 김미진, 김영재, 조웅희, 박상현, 김준형', '코미디', '100분', '2021-04-19 12:25:15', '2018.02.21 - 2021.04.30', '7세 이상', '3', 14),
	(27, 1, '흉터', '대학교 때부터 사랑과 우정을 이어온 사회 초년생이 된 동훈, 재용, 지은\r\n복잡미묘한 관계 속에서 위태로운 등산을 시작한다. 등산 중 지은은 돌발적인 사고로 인해 돌연 죽음을 맞이한다.\r\n8년 뒤... 재용과 동훈은 지은이가사고사로 죽었던 그 산을 다시 찾고, 등산 중 재용은 부상입으며, 엎친데 덮친격으로 갑작스럽게 길까지 잃게된다. 가까스로 발견한 검은 산장에서 그들은 구조를 기다린다.\r\n아무도 찾지 않는 비밀스런 산장에서 재용과 동훈은 지은의 기억이 되살아나고, 과거에 벌어졌던 끔찍한 사고의 기업이 결국 그들을 극한의 상황으로 몰아가는데...\r\n서서히 드러나는 공포의 실체와 잔인한 반전\r\n그날 그둘에게는 무슨일이 있었던 것일까..?\r\n공연포인트 알고보면 더 무서운공포연극, 순도 100% 리얼 공포\r\n', '다운로드 (5).jpg', '이종환, 이평화, 전청일, 강민주, 김진주, 유원진, 강동웅, 양승환, 엄현수', '공포', '80분', '2021-04-19 12:31:04', '2021.05.01 - 2021.05.30', '13세 이상 관람가', '3', 4),
	(28, 1, '연애하기좋은날', '우리 아 왜 헤어졌을까?\r\n연애를 조작하는 지후와 그에 대한 기억이라곤 쓰레기뿐인 시연\r\n나에게\r\n기회가 있었어?\r\n지후는 시연의 마음을 돌리기 위해 과거 연인시절의 이야기를 조작하기 시작한다. 서로에 대한 마음이 호감으로 변하는 와중 잊혀졌던 이별의 기억이 조금씩 돌아오는데!!!\r\n', '다운로드 (6).jpg', '이재혁, 최지훈, 차도하, 신아라, 박한울, 이성은, 김동현, 김초롱, 김영재', '로맨틱 코미디', '90분 ', '2021-04-19 12:36:09', '2019.10.03 - 2021.04.30', '만 13세 이상', '3', 1),
	(29, 1, 'The 가구', '못 산 자들이여, 망치를 들어라\r\n현경과 복희, 소민은 DIY 목공방의 회원이다. 현경은 열 살 아이의 엄마로 공방에서 아이 침대를 만들고 있고,\r\n복희는 자신을 위한 화장대를 만들고 있고, 소민은 이상하게 생긴 의자를 만들고 있다.\r\n셋은 겉으로는 밝고 즐겁지만 속마음은 그렇지 못하다. 현경은 남편을 철썩 같이 믿고 아이를 키우고 있지만 어딘가 불안하고,\r\n복희는 독립적인 여성상을 꿈꾸지만 남편과의 이혼을 고민 중이고, 제일 어리지만 일찍 결혼해 주부의 길로 들어선 소민은 정신과 상담을 받고 있다.\r\n이들은 공방에서 수다를 떤다. 유일한 안식처이자 피난처인 목공방의 모임. 그리고 가구를 만들어 가면서 상처투성이인 자아를 발견한다.\r\n', '다운로드 (7).jpg', '손고명, 이일진, 윤영민, 김나우, 임윤비, 이지현, 김윤아, 황현빈, 진유빈, 강이슬, 한아름', '가족, 코미디', '90분', '2021-04-19 12:41:03', '2020.06.19 - 2021.12.31', '12세 이상', '3', 20),
