DROP TABLE IF EXISTS `boardview`;
CREATE ALGORITHM=UNDEFINED SQL SECURITY DEFINER VIEW `boardview` AS select `board`.`views` AS `views`,(select truncate(sum(`review`.`r_rate`) / count(`review`.`r_rate`),2) from `review` where `board`.`bno` = `review`.`bno`) AS `average`,(select sum(`review`.`r_rate`) from `review` where `board`.`bno` = `review`.`bno`) AS `total`,(select count(0) from `review` where `board`.`bno` = `review`.`bno`) AS `COUNT`,`board`.`bno` AS `bno`,`board`.`member_no` AS `member_no`,`board`.`title` AS `title`,`board`.`content` AS `content`,`board`.`actor` AS `actor`,`board`.`genre` AS `genre`,`board`.`runtime` AS `runtime`,`board`.`rundate` AS `rundate`,`board`.`grade` AS `grade`,`board`.`file` AS `file`,`board`.`type` AS `type` from `board`;

